sys_variable_value.value:
(function execute(inputs, outputs) {

	if (inputs.Status == 200 || inputs.Status == 204) {
		outputs.status = "Success";

	} else {
		outputs.status = "Error";
		outputs = new AzureNotificationHubErrorHandler().updateInstallation(inputs, outputs);
	}
})(inputs, outputs);

