sys_variable_value.value:
(function execute(inputs, outputs) {
	var obj = JSON.parse(inputs['responseHeader']);
    var registerID = obj.Location.toString();
    registerID = registerID.split('/');
    registerID = registerID[5].split('?');

	if (inputs.Status == 201 || inputs.Status == 200) {
		outputs.status = "Success";
        outputs.regid = registerID[0];

	} else {
		outputs.status = "Error";
		outputs = new AzureNotificationHubErrorHandler().notificationErrors(inputs, outputs);
	}
})(inputs, outputs);

