sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs.BodyTemplate == ""){
outputs.payload = 
"<?xml version=1.0 encoding=utf-8?>"+
"<entry xmlns=http://www.w3.org/2005/Atom>"+
    "<content type=application/xml>"+
        "<AppleRegistrationDescription xmlns:i=http://www.w3.org/2001/XMLSchema-instance" xmlns="http://schemas.microsoft.com/netservices/2010/10/servicebus/connect>"
            "<Tags>"+inputs.Tags+"</Tags>"+
            "<DeviceToken>"+inpts.DeviceToken+"</DeviceToken>"+ 
        "</AppleRegistrationDescription>"+
    "</content>"+
"</entry>";
  }
 else if(inputs.BodyTemplate != ""){
   outputs.payload = 
"<?xml version=1.0 encoding=utf-8?>"+
"<entry xmlns=http://www.w3.org/2005/Atom>"+
    "<content type=application/xml>"+
        "<AppleTemplateRegistrationDescription xmlns:i=http://www.w3.org/2001/XMLSchema-instance" xmlns="http://schemas.microsoft.com/netservices/2010/10/servicebus/connect>"+
            "<Tags>"+inputs.Tags+"</Tags>"+
            "<DeviceToken>"+inpts.DeviceToken+"</DeviceToken>"+
            "<BodyTemplate><![CDATA["+inputs.BodyTemplate+"]]></BodyTemplate>"+
            "<Expiry>"+inputs.Expiry+"</Expiry>"+
        "</AppleTemplateRegistrationDescription>"+
    "</content>"+
"</entry>";
  }
})(inputs, outputs);

