sys_variable_value.value:
(function execute(inputs, outputs) {
	var obj = JSON.parse(inputs['ResponseHeaders']);
	outputs.etag = obj.ETag;

	if (inputs.Status == 201) {
		outputs.status = "Success";

	} else {
		outputs.status = "Error";
		outputs = new AzureNotificationHubErrorHandler().notificationErrors(inputs, outputs);
	}
})(inputs, outputs);

