sys_variable_value.value:
(function execute(inputs, outputs) {
 var results = new AzureNotificationValidation().inputValidation(inputs);
  results = JSON.stringify(results);
	results = JSON.parse(results);
	outputs.subscriptionid = results.Namespace;
	outputs.resourcegroupname = results.NotificationHub;

 var gdt = new GlideDateTime();
  outputs.apns_time = gdt; 
  var gtime1 = new GlideTime();
gtime1.setValue("00:00:20");
  gdt.add(gtime1);
  outputs.new_time = gdt.getDateTime(); 

})(inputs, outputs);

