sys_variable_value.value:
(function execute(inputs, outputs) {
if (inputs.Status == 200) {
		outputs.status = "Success";
      outputs.error_message = 'Message successfully sent.';

	} else {
		outputs.status = "Error";
		outputs = new AzureNotificationHubErrorHandler().comErrors(inputs, outputs);
	}
})(inputs, outputs);

