sys_variable_value.value:
(function execute(inputs, outputs) {
    var sku = {};
    var skuIn = inputs.Sku;

    if (skuIn != "") {
        var skuNames = inputs.Sku.split('^');
        for (var i = 0; i < skuNames.length; i++) {
            var skuLog = skuNames[i];
            var skuSegregate = skuLog.split('=');
            sku[skuSegregate[0]] = skuSegregate[1];

        }
        new AzureNotificationValidation().inputValidation(sku);
    }
    var properties = {};
    var propIn = inputs.Properties;
    if (propIn != "") {
        var propertyNames = inputs.Properties.split('^');
        for (var i = 0; i < propertyNames.length; i++) {
            var propLog = propertyNames[i];
            var propSegregate = propLog.split('=');
            properties[propSegregate[0]] = propSegregate[1];
        }

        new AzureNotificationValidation().inputValidation(properties);
    }
    var skuObj = {};
  if (inputs.Location != "") {
    skuObj['location'] = inputs.Location;
  }
    if (propIn != "") {
        skuObj['properties'] = properties;
    }
    if (skuIn != "") {
        skuObj['sku'] = sku;
    }
    outputs.sku = JSON.stringify(skuObj);


})(inputs, outputs);

