sys_variable_value.value:
(function execute(inputs, outputs) {
	var results = new AzureNotificationValidation().inputValidation(inputs);
	results = JSON.stringify(results);
	results = JSON.parse(results);
	outputs.subscriptionid = results.SubscriptionID;
	outputs.resourcegroupname = results.ResourceGroupName;
	outputs.namespace = results.Namespace;

})(inputs, outputs);

