})(current);

sys_variable_value.value:
(function execute(inputs, outputs) {
    var obj = JSON.parse(inputs['ResponseBody']);
    if (inputs.StatusCode == 200) {
        var notificationhubs = obj.value.map(function(item) {

            return {
                label: item.name,
                name: item.name
            };
        });
        outputs.notificationhubs = {
            data: notificationhubs
        };
    } else {
        outputs.notificationhubs = {
            data: []
        };
    }
})(inputs, outputs);

