sys_variable_value.value:
(function execute(inputs, outputs) {

	if (inputs.Status == 200 || inputs.Status == 201 || inputs.Status == 202) {
		outputs.status = "Success";

	} else {
		outputs.status = "Error";
		outputs = new AzureNotificationHubErrorHandler().notificationErrors(inputs, outputs);
	}
})(inputs, outputs);

