sys_variable_value.value:
(function execute(inputs, outputs) {
	var body = {};
	if (inputs.templateBody != '') {
		body = {
			installationId: inputs.installationID,
			tags: inputs.tags,
			platform: inputs.platform,
			pushChannel: inputs.platform,
			templates: {
				Template: {
					body: inputs.templateBody,
					expiration: inputs.templateExpiry,
					tags: inputs.templateTags
				}
			}

		}
	} else {
		body = {
			installationId: inputs.installationID,
			tags: inputs.tags,
			platform: inputs.platform,
			pushChannel: inputs.platform
		}
	}
	outputs.jscript = JSON.stringify(body);
})(inputs, outputs);

